
public class Hello {
	
	void method() 
	{
		int i = 0;
		while(i < 10)
		{
			i++;
			System.out.println("Value = " +  i);
		}
	}
	
	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
        Hello test = new Hello();
        test.method();
	}


	    

}
